
DROP TABLE NOVELINFO;

CREATE TABLE NOVELINFO(
    NNO NUMBER(20) PRIMARY KEY,
    NGENRE VARCHAR2(10),
    NTITLE VARCHAR2(100),
    FREE VARCHAR2(20) CHECK(FREE IN('TRUE','FALSE')),
    NWRITER VARCHAR2(30),
    NSIMPCONTENT VARCHAR2(4000),
    TOTAL NUMBER(20),
    NVIEW NUMBER(30),
    IMG VARCHAR2(100),
    COMPLATE VARCHAR2(10),
    PAY NUMBER(30)
);

drop sequence NOVEL_SEQ;
create sequence NOVEL_SEQ start with 1 increment BY 1 maxvalue 10000;


--무료소설
INSERT INTO NOVELINFO VALUES (NOVEL_SEQ.NEXTVAL,'로맨스','눈물의 은메달','TRUE','연성흠',' 명길이는 다른 날보다 기운이 퍽 나아진 것 같았다. 수남이가 명길의 병실 로 들어가니까 이야기도 재미있게 하고 그 얼굴에는 기쁜 빛이 넘쳐 있었다. 살은 몹시 말랐을망정 그 하얗게 세이다시피 한 얼굴에는 기뻐하는 빛이 떠돌고 두 뺨은 퍽도 불그레하였다. 참말로 더 귀여워 보였다.',3,3000,'/SalmonBooks/resource/image/novelimage/37170406016_63d0b3ebd7.jpg','FALSE',0);
-----일단 여기까지 삽입
INSERT INTO NOVELINFO VALUES (NOVEL_SEQ.NEXTVAL,'판타지','먹으면죽는사탕','TRUE','연성흠',' 어떠한 시골 동리에 욕심 많은 글방 선생님이 한 분 계셨습니다. 어느 날 얼음사탕 한 봉지를 서울 갔다 온 동리 사람에게 선물로 받았습니다. 선생님 은 자기가 없는 동안에 이것을 글방 아이들이 꺼내 먹을까 보아서 “얘들아! 이 상자 속에 넣어 둔 종이 봉지 속에는 먹기만 하면 당장 죽는 약이 들었으니 일절 손도 대지 말아라.” 하고 여러 아이들에게 일러두었습니다.',3,3000,'/SalmonBooks/resource/image/novelimage/4116899138_1e57ebe074.jpg','FALSE',0);
INSERT INTO NOVELINFO VALUES (NOVEL_SEQ.NEXTVAL,'판타지','못난쇠돌이','TRUE','연성흠','쓸쓸한 시골 자그마한 동리에 이 동리에서 제일 첫째가는 부자가 살았는데 그 부자의 이름은 그저 김 서방이라고만 동리 사람들이 불렀습니다. 돈 많은 부자이건만 욕심 많고 인색하기로도 이름난 사람이었습니다. 김 서방은 그 슬하에 아들 둘을 두었는데 큰아들의 이름은 쇠돌이고 작은아들의 이름은 귀복이었습니다',3,3000,'/SalmonBooks/resource/image/novelimage/2505514236_ecc9ec4f31.jpg','FALSE',0);
INSERT INTO NOVELINFO VALUES (NOVEL_SEQ.NEXTVAL,'무협','참된용자','TRUE','연성흠','삼보는 올에 14살 된 소년입니다. 워낙 마음씨가 좋지 못한데다가 장난이 무척 심하여 동리 안에서 장난괴수라는 별명까지 듣습니다. 마분지로 모자를 만들어 쓰고 허리에는 당목 테로 만든 칼을 차고 2, 30명이나 되는 어린애들 을 데리고 그 동리 뒤에 있는 동산에 올라가서 온갖 못된 짓을 모조리 다 하였습니다. 어두운 밤중에 길거리에다 함정을 만들어놓기가 일쑤이고 길 가는 소년을 붙잡고 싸움 걸기는 보통이고, 더구나 자기가 데리고 다니는 2, 30명이나 되 는 어린애들을 함부로 윽박지르면서 제가 잘난 체하고 마구 뽐내었습니다.',3,3000,'/SalmonBooks/resource/image/novelimage/15705742991_a293e912a8.jpg','FALSE',0);
INSERT INTO NOVELINFO VALUES (NOVEL_SEQ.NEXTVAL,'판타지','눈물의맹세','TRUE','최병화',' 유월의 태양! 모든 자연이 우쩍우쩍 자라고 뻗어 나가는 유월의 태양은 오 늘도 아침부터 OO심상소학교를 내려 비치기 시작하였습니다. 운동장 주위에 하늘을 뚫을 듯이 높이 솟은 포플러 나무라든지 교실 창 앞 에 일자 지게 죽 늘어선 벚꽃 나무라든지 화단에 심은 여러 가지 여름 꽃나 무라든지 모든 게 햇빛을 실컷 받아서 그 싱싱한 품이 금방초록 물이 뚝뚝 떨어질 듯이 보였습니다. 이러한 교정에서 싱싱한 자연과 더불어 씩씩하게 뛰놀고 있는 이 학교 생 도들은 오늘도 다른 날과 변함이 없이 유쾌하게 뛰놀고 있었습니다.',3,3000,'/SalmonBooks/resource/image/novelimage/10702961054_0727de9c59.jpg','FALSE',0);
INSERT INTO NOVELINFO VALUES (NOVEL_SEQ.NEXTVAL,'무협','이상한중','TRUE','최병화','지금으로부터 몇백 년 전 서울 종로 네거리 종각 앞에 남루한 옷을 입은 늙은 중 한 사람이 홀연 나타났습니다. 어느 절에서 왔는지 또 이름을 무엇 이라고 하는지 아는 사람은 한 사람도 없었습니다. 이 중은 여름 겨울 할 것 없이 회색 장삼을 한 벌 입고 누런 명주 수건을 어깨에 둘렀습니다. 머리털은 길길이 자라서 새 깃과 같고 눈은 움푹 들어가 서 몇 끼 굶은 사람같이 보였습니다. 그리고 공연히 거리를 왔다 갔다 하며 밤에는 남의 집 처마 끝에서 자는데 이상한 것은 겨울에 눈이 와도 이 중 근처에는 쌓이지 않았습니다.',3,3000,'/SalmonBooks/resource/image/novelimage/2465403519_9a6e7fd3d.jpg','FALSE',0);
INSERT INTO NOVELINFO VALUES (NOVEL_SEQ.NEXTVAL,'무협','입학시험','TRUE','최병화','경부선 차는 이제 서울을 떠나려고 기적을 길게 울렸습니다. 무거운 차체는 흔들리고 바퀴는 천천히 구르기 시작합니다. 수건을 흔드는 사람에 모자를 흔드는 사람에 또는 손을 잡은 채 차를 따라가는 사람에 그들은 모두 이별 하기를 서러워하는 애틋한 빛이 넘쳐흘렀습니다. 이같이 사람의 따듯한 정을 찾아볼 수 있는 곳에서 두 소년의 이별은 더욱 아름다웠고 애달팠습니다. “종득이! 너무 낙심 말고 잘 내려가게. 그리고 선생님과 동리 어른들께도 문 안 여쭈어주게.” “재룡이! 나는 무슨 낯으로 선생님과 동리 어른을 대할는지 지금은 모든 것 이 귀찮고 부끄러워만 지네.',3,5000,'/SalmonBooks/resource/image/novelimage/4554697553_5beefdd596.jpg','FALSE',0);
INSERT INTO NOVELINFO VALUES (NOVEL_SEQ.NEXTVAL,'로맨스','참된우정','TRUE','최병화','진수는 서울 계신 아저씨께로부터 우등 진급하였다는 상으로 5원짜리 돈표 를 받았습니다. “1원만 가지고 사고 싶은 것을 사 가지고 남는 것은 저금하여 두어라.” 하고 어머니께서는 기쁜 웃음을 웃으시며 말씀하시었습니다. 진수는 그 말 씀을 듣고 곧 서점으로 뛰어갔습니다. 진수는 항상 남이 보던 헌 잡지만 빌 려보며 그 책 속에 실린 자기가 지은 동요와 혹은 작문을 보면서 혼자 기뻐 하였습니다. 이번에는- 내 돈으로 새 책을 살 수가 있다. 진수는 기쁨에 뛰노 는 가슴을 부둥켜안고 서점으로 뛰어가는 것입니다. 그러나 얄궂게도 진수의 글이 실린 XXX잡지는 다 팔리고 말았습니다. 진수는 실망이 되었지만 발을 돌이켜 나오다가 불현듯 누이동생 진순이를 생각하였습니다. “오빠! 나도 잡지 한 권 사다 주"',3,3000,'/SalmonBooks/resource/image/novelimage/23892466023_eece6eb5a3.jpg','FALSE',0);
INSERT INTO NOVELINFO VALUES (NOVEL_SEQ.NEXTVAL,'로맨스','금붕어','TRUE','최학송','북경으로 동지사가 들어갈 때였다.복석이는 짐을 지고 동지사 일행을 따라가게 되었다.“언제 돌아오련?”“글쎄, 내야 알겠니?”“그때 치맛감 한 감 꼭 사오너라.”“시끄러운 것. 두 번 부탁 안 해두 어련히 안 사오리.”복석이와 용녀의 작별은 눈물겨운 장면이었다. 놓았다가는 다시 부여잡고부여잡았다가는 다시 놓고 밤을 새워가면서 서로 울었다.',3,5000,'/SalmonBooks/resource/image/novelimage/4694899147_92af625855.jpg','FALSE',0);
INSERT INTO NOVELINFO VALUES (NOVEL_SEQ.NEXTVAL,'로맨스','눈보라치는날','TRUE','최학송','그것은 눈보라치는 날이었습니다. 은동이가 아버지 심부름을 갔다가 돌아오는 길에 긴 논둑길을 걸어올 때였 습니다. 마침 눈은 퍼부어 오시고 그 위에 눈보라까지 쳐서 뛰어갈 때였습니 다. 은동이는 별안간 무엇을 보았는지 “악!” 소리를 지르고 가던 걸음을 딱 멈추며 대여섯 간통 앞을 자세히 보았습니다. 그리고 은동이는 그곳에서 무 슨 놀라운 일을 발견하였는지 급한 걸음으로 뛰어갔습니다. 그리고 그 높은 논둑 언덕에 쌓인 눈 위를 미끄러져 내려가서 개골창에 넘 어진 소년에게로 갔습니다.',3,5000,'/SalmonBooks/resource/image/novelimage/45175500695_d7c1db2938.jpg','FALSE',0);

--유료소설
